ftware.', '[0.02723413,0.04557474,0.014062996,0.0012006211,0.006136343,0.021997666,0.055882297,0.031202251,-0.0239837,-0.0105771385,0.011606878,-0.010947416,0.038647547,-0.033508033,0.00957898,-0.01936467,0.051424842,0.055632923,-0.021329388,-0.03963581,-0.00061032793,-0.0139948325,-0.06044385,-0.057223197,-0.046426557,-0.008709849,0.0564854,-0.0032414699,0.0023708954,-0.013116001,0.026853038,0.006420831,-0.020352812,-0.05916209,0.036776084,0.038333144,0.015986722,0.03208193,0.02465094,-0.026259763,-0.029420901,-0.055306617,-0.062050346,0.03006067,-0.025701923,0.0038191944,0.027851198,0.0345284,-0.041830283,-0.003543029,0.011847106,-0.0060240007,-0.005244489,0.04955664,-0.02519569,-0.041481283,-0.045651685,-0.020287573,0.016414871,-0.038812622,-0.042727254,0.023260705,0.006581476,-0.019419849,-0.03838463,-0.007204734,-0.034585577,-0.009747972,-0.038442124,-0.018323915,-0.02174968,0.037162635,-0.0857119,0.01822049,0.07408286,-0.069761805,0.008914189,-0.055134997,0.009117367,0.01693418,0.0013092464,0.0454639,0.09025731,-0.0028513419,0.02154243,0.030312927,-0.0045696935,-0.07233921,-0.020521803,-0.013600293,0.026021775,0.016184399,-0.031163909,0.01703318,0.016329437,-0.043743376,-0.08694337,-0.08527224,0.018532006,0.025737744,0.019939454,0.047577426,-0.03345807,-0.011474719,0.042419754,-0.032434344,-0.059554238,-0.05484325,-0.12269714,0.03625666,-0.0076848194,0.0111324545,0.030032672,0.023665499,-0.0021576271,0.004358258,0.03317938,0.024250576,-0.057324294,0.009902719,0.052585594,0.020950047,8.4357926e-5,0.06896761,0.05115703,-0.061611384,-0.008573314,-0.013852688,0.03547961,0.028460877,0.047657456,-0.009730445,-0.047905236,0.02606286,0.0004359243,0.00089507975,0.03542484,-0.029733036,0.008072254,0.012136694,-0.03644471,-0.008861283,-0.13455035,0.05064676,0.00044380777,-0.049298007,0.08666698,0.020506404,-0.036272537,-0.0037264626,-0.038910996,0.03260872,0.025485966,0.014586482,0.009405806,-0.027386196,0.010119576,-0.01429914,0.012231375,-0.020021137,0.08637553,-0.0009